// RTMP connection events
if (data.type === SSEEventType.RTMP_CONNECTED || data.type === SSEEventType.RTMP_DISCONNECTED) {
  if (data.eventId === eventId || data.id === eventId) {
    setEventDetailData((prev) => {
      if (!prev) return null;
      return {
        ...prev,
        event: {
          ...prev.event,
          streamStatus: data.streamStatus || (data.type === 'RTMP_CONNECTED' ? 'connected' : 'offline'),
        },
      };
    });
  }
}

// HLS distribution events
if (data.type === SSEEventType.HLS_READY || data.type === SSEEventType.HLS_UNAVAILABLE || data.type === SSEEventType.HLS_PROCESSING) {
  if (data.eventId === eventId || data.id === eventId) {
    setEventDetailData((prev) => {
      if (!prev) return null;
      return {
        ...prev,
        event: {
          ...prev.event,
          hlsStatus: data.hlsStatus || (data.type === 'HLS_READY' ? 'ready' : data.type === 'HLS_PROCESSING' ? 'processing' : 'offline'),
          streamUrl: data.streamUrl || prev.event.streamUrl,
        },
      };
    });
  }
}
